parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "about": {
      "type": "string",
      "description": "Information about the contact, taken from LinkedIn"
    },
    "websiteSummary": {
      "type": "string",
      "description": "Information about the company, taken from the website"
    }
  }
}

{
  "type": "object",
  "required": ["action"],
  "properties": {
    "action": {
      "type": "string",
      "note": "Provide the action to perform, such as 'Create a draft' or 'Send an email'.",
      "value": "Create a draft"
    },
    "parameters": {
      "type": "object",
      "note": "Provide the necessary details for the email action.",
      "properties": {
        "senderEmailAddress": {
          "type": "string",
          "note": "The email address of the sender.",
          "value": "sender@example.com"
        },
        "recipientEmailAddress": {
          "type": "string",
          "note": "The recipient's email addresses, separated by commas if multiple.",
          "value": "recipient1@example.com,recipient2@example.com"
        },
        "ccRecipientEmailAddress": {
          "type": "string",
          "note": "CC email addresses, separated by commas if multiple.",
          "value": "cc@example.com"
        },
        "bccRecipientEmailAddress": {
          "type": "string",
          "note": "BCC email addresses, separated by commas if multiple.",
          "value": "bcc@example.com"
        },
        "subject": {
          "type": "string",
          "note": "The subject of the email.",
          "value": "Meeting Follow-Up"
        },
        "body": {
          "type": "string",
          "note": "The content of the email.",
          "value": "Hi Team,\n\nThank you for attending the meeting. Here are the action items:\n\n1. Follow up on the project.\n2. Schedule the next meeting.\n\nBest regards,\nJohn"
        },
        "messageId": {
          "type": "string",
          "note": "The ID of the message (useful for replying or forwarding).",
          "value": "12345abcdef"
        },
        "labelId": {
          "type": "string",
          "note": "The ID of the label to associate with the message.",
          "value": "label_001"
        },
        "replyToSenderOnly": {
          "type": "boolean",
          "note": "Set to true if replying only to the sender.",
          "value": true
        },
        "search": {
          "type": "string",
          "note": "A search query for finding messages.",
          "value": "project updates"
        },
        "readStatus": {
          "type": "string",
          "note": "The read status of the email ('unread', 'read', or 'both').",
          "value": "unread"
        },
        "receivedAfter": {
          "type": "string",
          "note": "Filter emails received after this date (ISO 8601 format).",
          "value": "2024-01-01T00:00:00Z"
        },
        "receivedBefore": {
          "type": "string",
          "note": "Filter emails received before this date (ISO 8601 format).",
          "value": "2024-12-31T23:59:59Z"
        },
        "senderName": {
          "type": "string",
          "note": "The name of the sender.",
          "value": "John Doe"
        },
        "newLabelName": {
          "type": "string",
          "note": "The name of the new label to create.",
          "value": "Important"
        },
        "threadId": {
          "type": "string",
          "note": "The ID of the email thread.",
          "value": "thread_001"
        },
        "draftId": {
          "type": "string",
          "note": "The ID of the draft.",
          "value": "draft_123"
        },
        "maxResults": {
          "type": "integer",
          "note": "The maximum number of messages to retrieve (limit: 50).",
          "value": 10,
          "maximum": 50
        }
      }
    }
  }
}
