// n8n Code Node JavaScript
// Ensure this Code node is set to "Run Mode: Run Once For All Items"
// (or "Run Once For Each Item" if you are certain only one item passes through here at a time)

// Get all input items. If "Run Once For Each Item", use $input.item() and remove the loop.
const items = $input.all();
const outputItems = [];

for (const item of items) {
  const attachmentsDetails = [];

  // Check if the current item has binary data and if there are any attachments
  // The Gmail node (with download attachments) puts files directly into item.binary
  if (item.binary && Object.keys(item.binary).length > 0) {
    // Iterate over each attachment key in item.binary
    // These keys are like 'data', 'data0', 'data1', or sometimes the filename
    for (const attachmentKey in item.binary) {
      if (item.binary.hasOwnProperty(attachmentKey)) {
        const attachmentData = item.binary[attachmentKey]; // Object containing details of one attachment

        // Construct an object with the desired information
        const detail = {
          attachment_id: attachmentKey, // The key in the item.binary object
          filename: attachmentData.fileName,
          file_extension: attachmentData.fileExtension, // e.g., 'pdf', 'png'
          file_type: attachmentData.mimeType,         // e.g., 'application/pdf', 'image/png' (MIME type)
          file_size: attachmentData.fileSize,         // File size in bytes (numeric)
          // file_size_readable: (attachmentData.fileSize / 1024).toFixed(2) + ' KB' // Optional human-readable size
        };
        attachmentsDetails.push(detail);
      }
    }
  }

  // Add the list of attachment details to the current item's JSON data.
  // If there were no attachments, this will add an empty array.
  item.json.attachments = attachmentsDetails;
  outputItems.push(item);
}

// Return all items, now modified to include the 'attachments' array in their JSON part
return outputItems;

// n8n Code Node: Split attachments array into individual items
// Mode: Run Once For All Items

// This code expects each input item to potentially have:
// - item.json: Contains an 'attachments' array and other parent data (e.g., email subject).
// - item.json.attachments: An array where each element is an object describing an attachment
//   (e.g., { attachment_id, filename, file_type, file_size, ... }).
// - item.binary: An object where keys are the 'attachment_id' from the above objects,
//   and values are the n8n binary data objects for those attachments.

const allInputItems = $input.all();
const newOutputItems = [];

for (const currentItem of allInputItems) {
  // Check if the item has a non-empty 'attachments' array in its JSON property
  if (currentItem.json && Array.isArray(currentItem.json.attachments) && currentItem.json.attachments.length > 0) {

    // Prepare parent JSON data to be copied to each new attachment item.
    // This excludes the 'attachments' array itself.
    const parentJsonFields = { ...currentItem.json };
    delete parentJsonFields.attachments;

    // Iterate over each attachment object in the 'attachments' array
    for (const attachmentObject of currentItem.json.attachments) {
      // Create the JSON for the new item.
      // Start with a copy of the parent item's JSON fields (e.g., subject, messageId),
      // then merge/overwrite with the attachment's specific details.
      const newJson = {
        ...parentJsonFields, // Fields from the original email/item
        ...attachmentObject    // Fields specific to this attachment (filename, file_type, etc.)
      };

      // Prepare the binary data for the new item
      const newBinary = {};
      // 'attachment_id' is the key used to link the JSON description to the binary data
      // in the original item (e.g., "data0", "attachment_0" from the Gmail node, or the value you created).
      const attachmentId = attachmentObject.attachment_id; 

      // Check if the original item has binary data and if the specific attachment's binary exists
      if (attachmentId && currentItem.binary && currentItem.binary.hasOwnProperty(attachmentId)) {
        // Assign the attachment's binary data to a standard key, e.g., 'file'.
        // This makes it easier for downstream nodes that expect a single binary property per item.
        // The object currentItem.binary[attachmentId] already contains { fileName, mimeType, data, ... }
        newBinary.file = currentItem.binary[attachmentId];
      } else if (attachmentId) {
        // Log a warning if binary data is expected but not found
        const originalMessageId = parentJsonFields.id || parentJsonFields.messageId || 'N/A';
        console.warn(`For item (original ID: ${originalMessageId}), binary data for attachment_id '${attachmentId}' was not found in original item.binary. The new item for this attachment will not have binary data.`);
      } else {
        const originalMessageId = parentJsonFields.id || parentJsonFields.messageId || 'N/A';
        console.warn(`For item (original ID: ${originalMessageId}), an attachment object was found without an 'attachment_id'. Cannot retrieve its binary data. Attachment object: ${JSON.stringify(attachmentObject)}`);
      }

      // Create the new item (representing a single attachment) and add it to our list
      newOutputItems.push({
        json: newJson,
        binary: newBinary
      });
    }
  } else {
    // This section handles items that don't have an 'attachments' array or have an empty one.
    // Current behavior: Only items derived from attachments are outputted.
    // If you want to pass through original items that had no attachments, you would uncomment:
    // newOutputItems.push(currentItem);
    let logMessage = "Item will not be processed for attachment splitting: ";
    if (!currentItem.json) {
        logMessage += "No JSON property found.";
    } else if (!Array.isArray(currentItem.json.attachments)) {
        logMessage += "JSON property 'attachments' is not an array.";
    } else if (currentItem.json.attachments.length === 0) {
        logMessage += "JSON property 'attachments' is an empty array.";
    }
    const originalMessageId = currentItem.json ? (currentItem.json.id || currentItem.json.messageId || 'N/A') : 'N/A (no JSON)';
    console.log(`${logMessage} (Original ID: ${originalMessageId})`);
  }
}

// Return the list of new items. Each item now represents a single attachment.
return newOutputItems;
